import socket
import time

port = 8888
s = socket.socket(socket.AF_INET,socket.SOCK_DGRAM)
s.bind(("",port))
print('waiting on port:', port)
while True:
	data, addr = s.recvfrom(1024)
	print("DATA:", data, addr)
	time.sleep(100)

